  "Interest Calculation": {
    "Formula": "Sum of Products × Rate × Time",
    "Example Calculation": "Interest = Rs. 4,30,000 × 30/100 × 1/12 = Rs. 2,508 (approx.)",
    "Financial Year": "2024-25"
  },
  "Illustration 8": {
    "Title": "Accountancy - Partnership Accounts",
    "Context": "John Ibrahm, a partner in Modern Tours and Travels, withdrew money during the year ending March 31, 2020, from his capital account for his personal use. Calculate interest on drawings in each of the following alternative situations, if the rate of interest is 9 percent per annum.",
    "Situations": {
      "(a)": "If he withdrew Rs. 3,000 per month at the beginning of the month.",
      "(b)": "If an amount of Rs. 3,000 per month was withdrawn by him at the end of each month.",
      "(c)": "If the amounts withdrawn were Rs. 12,000 on June 01, 2019, Rs. 8,000 on August 31, 2019, Rs. 3,000 on September 30, 2019, Rs. 7,000 on November 30, 2019, and Rs. 6,000 on January 31, 2020."
    },
    "Solution": {
      "(a)": {
        "Description": "As a fixed amount of Rs. 3,000 per month is withdrawn at the beginning of the month, interest on drawings will be calculated for an average period of 6 months.",
        "Calculation": "Interest on drawings = Rs. 36,000 × 9 × 6/100 × 1/12 = Rs. 1,755"
      },
      "(b)": {
        "Description": "As the fixed amount of Rs. 3,000 per month is withdrawn at the end of each month, interest on drawings will be calculated for an average period of 5.5 months.",
        "Calculation": "Interest on drawings = Rs. 36,000 × 9 × 5.5/100 × 1/12 = Rs. 1,485"
      },
      "(c)": {
        "Description": "Statements showing Calculation of Interest on Drawings."
      }
    }
  }
}

